import { useState } from 'react';
import { StyleSheet, Text, View, TextInput, Button } from 'react-native';
import Jogo from './Jogo';
import JogoMemoria from './JogoMemoria';

export default function Nomes({
  mudarNomeJogadores,
  changeScreen
}) {
  const [jogador1, setJogador1] = useState("");
  const [jogador2, setJogador2] = useState("");

  const handleClick = () => {
    if(mudarNomeJogadores) {
        mudarNomeJogadores(jogador1, jogador2)
        changeScreen("jogo")
    }
  }

  const handleClick2 = () => {
    if(mudarNomeJogadores) {
        mudarNomeJogadores(jogador1, jogador2)
        changeScreen("JogoMemoria")
    }
    
  }

  return (
    <View style={styles.container}>

      {/* <Text>O nome do jogador 1 é: {jogador1}</Text> */}
      <TextInput 
        placeholder='Jogador 1' 
        value={jogador1} 
        onChangeText={setJogador1}
      />

      {/* <Text>O nome do jogador 2 é: {jogador2}</Text> */}
      <TextInput 
        placeholder='Jogador 2' 
        value={jogador2} 
        onChangeText={setJogador2} 
      />

      <View style={styles.buttons}>
        <Button 
          title='Iniciar jogo da velha' 
          onPress={handleClick} 
          color='#09bd9c'
        />
        <Button 
          title='Iniciar jogo da memoria' 
          onPress={handleClick2} 
        />
      </View>
   

    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    gap: 10,  
    backgroundColor: '#9effd3',
    alignItems: 'center',
    justifyContent: 'center',
    margin: 200,
  },
  buttons: {
    flex: 1,
    gap: 8,
  },
})
